#include<iostream>
#include<math.h>
#include<new>

using namespace std;

const int A=20;		//pocet aut v zavodu
const int R1=150;	//minimanlni pocatecni prumerna rychlost
const int R2=180;	//maximalni pocatecni prumerna rychlost
const int P=10;		// procento zmenseni prumerne rychlosti pri oprave
const int M=120;	// delka zavodu v minutach
int pom=1;

class Auto
{
private:
	int cislo;
	double average, zpozdeni,vzdalenost;


public:
	Auto();
	~Auto();
	int Cislo();
	int Pokracovat();
	int Zastavit();
	int Opravit();
	double Vzdalenost();

};

Auto::Auto()
{
	average=(rand()%(R2-R1)+R1)/100;
	zpozdeni=rand()%P;
	vzdalenost=0;
	cislo=pom;
	pom++;
}

Auto::~Auto()
{
	average=NULL;
	zpozdeni=NULL;
	vzdalenost=NULL;
	cislo=NULL;
}

int Auto::Cislo()
{
	return cislo;
}

int Auto::Pokracovat()
{
	vzdalenost+=average/60;
	return 0;
}

int Auto::Zastavit()
{
	return 0;
}

int Auto::Opravit()
{
	average-=average*zpozdeni;
	return 0;
}

double Auto::Vzdalenost()
{
	return vzdalenost;
}

